package build.wallet.bitkey.keys.app

import build.wallet.crypto.KeyPair
import build.wallet.crypto.KeyPurpose
import build.wallet.crypto.PrivateKey
import build.wallet.crypto.PublicKey

/**
 * An app key is a key that is generated by the app and stored in the app's keystore.
 */
data class AppKey<T : KeyPurpose>(
  override val publicKey: PublicKey<T>,
  override val privateKey: PrivateKey<T>,
) : KeyPair<T>
